INSERT INTO t_p95354559_review_sentiment_ana.response_templates (name, tone, text, is_default) VALUES
('Благодарность (позитивный)', 'positive', 'Здравствуйте, {author}! Большое спасибо за тёплый отзыв — это очень ценно для нас. Рады, что смогли оправдать ваши ожидания, и будем рады видеть вас снова!', true),
('Извинение (негативный)', 'negative', 'Здравствуйте, {author}! Нам жаль, что ваш опыт оказался неудачным. Мы приносим искренние извинения и обязательно разберёмся в ситуации. Свяжитесь с нами напрямую — сделаем всё, чтобы исправить положение.', true),
('Нейтральный ответ', 'neutral', 'Здравствуйте, {author}! Спасибо, что поделились мнением. Мы внимательно изучим ваш отзыв и постараемся улучшить качество наших услуг. Будем рады снова видеть вас!', true),
('Приглашение к диалогу', 'neutral', 'Здравствуйте, {author}! Благодарим за отзыв. Для нас важно ваше мнение — свяжитесь с нами удобным способом, чтобы мы могли подробнее обсудить вашу ситуацию.', false);